mapper.sql:
select uuid from city where chinese_name = #{term} or name = #{term} or pinyin_name = #{term}
union
select uuid from province where chinese_name = #{term} or name = #{term} or pinyin_name = #{term}
union
select uuid from country where chinese_name = #{term} or name = #{term} or pinyin_name = #{term}

mapper.select:
select
from (
union select associated_location_uuid as uuid from location_association where hex(location_uuid) = replace(#{departureLocationUuid}, '-', '') ) as location join agency on hex(location.uuid) = hex(agency.send_location_uuid)
